// Get binary data from readBinaryFile node
const item = items[0];

if (!item.binary || !item.binary.data) {
  throw new Error('No binary data found. Check Read CSV File node.');
}

// n8n binary structure: binary.data contains the base64 string
let base64String;
const binaryData = item.binary.data;

// Handle different binary data structures
if (typeof binaryData === 'string') {
  base64String = binaryData;
} else if (binaryData.data && typeof binaryData.data === 'string') {
  base64String = binaryData.data;
} else {
  throw new Error('Unexpected binary data format');
}

// Decode base64 to get CSV text
let csvText;
try {
  const buffer = Buffer.from(base64String, 'base64');
  csvText = buffer.toString('utf8');
} catch (e) {
  // If base64 decode fails, try using as plain text
  csvText = base64String;
}

// Split into lines and filter empty lines
const lines = csvText.split(/\r?\n/).filter(line => line.trim().length > 0);

if (lines.length < 2) {
  throw new Error('CSV file has no data rows (only header or empty)');
}

// Parse CSV rows
// CSV format: HR Name, HR Email, HR Position, Company Name
const results = [];
for (let i = 1; i < lines.length; i++) {
  const line = lines[i].trim();
  if (!line) continue;

  // Split by comma and trim each value
  const values = line.split(',').map(v => v.trim());

  // Ensure we have all 4 columns
  if (values.length >= 4) {
    results.push({
      json: {
        hrName: values[0] || '',
        hrEmail: values[1] || '',
        hrPosition: values[2] || '',
        companyName: values[3] || ''
      }
    });
  }
}

if (results.length === 0) {
  throw new Error('No valid rows parsed from CSV');
}

return results;
